mapper.insert:

  SELECT NVL(MAX(CAST(FILE_ID AS UNSIGNED)), 0) + 1 
    FROM FILE_UPLOAD_INFO

INSERT INTO 
FILE_UPLOAD_INFO ( FILE_ID            /* 게시판구분코드 */    
                 , FILE_DIV_CD        /* 글번호 */            
                 , ORIGIN_FILE_NM     /* 작성일자 */          
                 , REAL_FILE_NM       /* 작성자아이디 */      
                 , FILE_PATH          /* 제목 */              
                 , FILE_SIZE          /* 파일사이즈 */
                 , USE_YN             /* 사용여부 */
                 , REG_USER_ID        /* 등록자 */            
                 , REG_DT             /* 등록일자 */          
                 )
          VALUES ( #{file_id}
                 , #{file_div_cd}
                 , #{origin_file_nm}
                 , #{real_file_nm}
                 , #{file_path}
                 , #{file_size}
                 , #{use_yn}
                 , #{user_id}
                 , DATE_FORMAT(now(), '%Y%m%d%H%i%s')
                 )

mapper.select:
SELECT FILE_ID
  FROM FILE_UPLOAD_INFO
 WHERE 1=1
   AND ORIGIN_FILE_NM = #{origin_file_nm}
   AND REAL_FILE_NM   = #{real_file_nm}
   AND FILE_DIV_CD    = #{file_div_cd}

SELECT A.BOARD_NO , A.BOARD_DIV_CD , (SELECT CODE_KNM FROM COM_CODE WHERE CD_GRP_ENM = 'BOARD_DIV_CD' AND CODE = A.BOARD_DIV_CD) AS BOARD_DIV_NM , A.USER_ID , (SELECT USER_KOR_NM FROM USER_INFO WHERE USER_ID = A.USER_ID) AS USER_KOR_NM , CONCAT(SUBSTRING(A.WRITE_DT, 3, 2),'-',SUBSTRING(A.WRITE_DT, 5, 2),'-',SUBSTRING(A.WRITE_DT, 7, 2)) AS WRITE_DT , A.BOARD_TITLE , A.BOARD_CNTN , A.BIBLE_INDEX_CNTN
, A.PHOTO_ID , C.REAL_FILE_NM , CONCAT(C.FILE_PATH,C.REAL_FILE_NM) AS IMG_PATH , A.SELECT_CNT , A.WORSHIP_ORDER_CD , A.VIDEO_DIV_CD , A.YOUTUBE_IFRM_SRC , (SELECT CODE_KNM FROM COM_CODE WHERE CD_GRP_ENM = 'WORSHIP_ORDER_CD' AND CODE = A.WORSHIP_ORDER_CD) AS WORSHIP_ORDER_NM , A.DEL_YN , A.REG_USER_ID , A.REG_DT , '1' AS FAVORIT_CNT FROM BOARD A INNER JOIN USER_INFO B ON A.USER_ID = B.USER_ID AND B.USE_YN = 'Y' LEFT JOIN FILE_UPLOAD_INFO C ON A.PHOTO_ID = C.FILE_ID
WHERE 1=1 AND A.DEL_YN = 'N' AND (A.BOARD_DIV_CD = #{board_div_cd}
OR A.BOARD_DIV_CD = #{board_div_cd2}
OR A.BOARD_DIV_CD = #{board_div_cd3}
)
AND A.WRITE_DT BETWEEN #{strt_write_dt} AND #{end_write_dt}
AND A.WORSHIP_ORDER_CD = #{worship_order_cd}
AND A.BOARD_TITLE LIKE CONCAT('%', #{board_title}, '%')
AND A.BOARD_CNTN LIKE CONCAT('%', #{board_cntn}, '%')
AND B.USER_KOR_NM LIKE CONCAT('%', #{user_kor_nm}, '%')
ORDER BY CAST(BOARD_NO AS UNSIGNED) DESC LIMIT #{start_index}, #{page_size}


SELECT COUNT(1) AS CNT
  FROM BOARD A
 INNER JOIN USER_INFO B 
    ON A.USER_ID = B.USER_ID
   AND B.USE_YN = 'Y'   
 WHERE 1=1
   AND DEL_YN = 'N'
   AND (A.BOARD_DIV_CD = #{board_div_cd}

       OR A.BOARD_DIV_CD = #{board_div_cd2}


       OR A.BOARD_DIV_CD = #{board_div_cd3}

      )

   AND A.WRITE_DT BETWEEN #{strt_write_dt} AND #{end_write_dt}


   AND A.WORSHIP_ORDER_CD = #{worship_order_cd}  


   AND A.BOARD_TITLE LIKE CONCAT('%', #{board_title}, '%')    


   AND A.BOARD_CNTN LIKE CONCAT('%', #{board_cntn}, '%') 


   AND B.USER_KOR_NM LIKE CONCAT('%', #{user_kor_nm}, '%')

mapper.insert:

    SELECT NVL(MAX(CAST(board_no AS UNSIGNED)), 0) + 1
      FROM BOARD
     WHERE BOARD_DIV_CD = #{board_div_cd}

INSERT INTO 
 BOARD ( BOARD_DIV_CD          /* 게시판구분코드 */    
       , BOARD_NO              /* 글번호 */            
       , WRITE_DT              /* 작성일자 */          
       , USER_ID               /* 작성자아이디 */      
       , BOARD_TITLE           /* 제목 */              

       , BOARD_CNTN            /* 내용 */              


       , BIBLE_INDEX_CNTN      /* 성경본문내용 */      


       , VIDEO_DIV_CD          /* 영상구분코드 */      


       , YOUTUBE_IFRM_SRC      /* 유뷰트 IFRAME 소스 */


       , WORSHIP_ORDER_CD      /* 예배순서코드 */      


       , VIDEO_ID              /* 영상 ID */      


       , PHOTO_ID              /* 이미지 ID */      

       , SELECT_CNT            /* 조회수 */            
       , DEL_YN                /* 삭제여부 */          
       , REG_USER_ID           /* 등록자 */            
       , REG_DT                /* 등록일자 */          
       )
VALUES ( #{board_div_cd}
       , #{board_no}
       , DATE_FORMAT(now(), '%Y%m%d')
       , #{user_id}
       , #{board_title}

       , #{board_cntn}


       , #{bible_index_cntn}


       , #{video_div_cd}


       , #{youtube_ifrm_src}


       , #{worship_order_cd}


       , #{video_id}


       , #{photo_id}

       , '0'
       , 'N'
       , #{user_id}
       , DATE_FORMAT(now(), '%Y%m%d%H%i%s')
       )


    SELECT NVL(MAX(CAST(reply_no AS UNSIGNED)), 0) + 1
      FROM BOARD_REPLY
     WHERE 1=1
       AND BOARD_DIV_CD = #{board_div_cd}
       AND BOARD_NO = #{board_no}

INSERT INTO 
BOARD_REPLY ( BOARD_DIV_CD          /* 게시판구분코드 */    
            , BOARD_NO              /* 글번호 */            
            , REPLY_NO              /* 댓글번호 */
            , WRITE_DT              /* 작성일자 */      
            , USER_ID               /* 작성자ID */              
            , REPLY_CNTN            /* 내용 */              
            , DEL_YN                /* 삭제여부 */          
            , REG_USER_ID           /* 등록자 */            
            , REG_DT                /* 등록일자 */          
            )
VALUES ( #{board_div_cd}
       , #{board_no}
       , #{reply_no}
       , DATE_FORMAT(now(), '%Y%m%d')
       , #{user_id}
       , #{reply_cntn}
       , 'N'
       , #{user_id}
       , DATE_FORMAT(now(), '%Y%m%d%H%i%s')
       )


    SELECT NVL(MAX(CAST(re_reply_no AS UNSIGNED)), 0) + 1
      FROM BOARD_RE_REPLY
     WHERE 1=1
       AND BOARD_DIV_CD = #{board_div_cd}
       AND BOARD_NO = #{board_no}
       AND REPLY_NO = #{reply_no}

INSERT INTO 
BOARD_RE_REPLY ( BOARD_DIV_CD          /* 게시판구분코드 */
               , BOARD_NO              /* 글번호 */            
               , REPLY_NO              /* 댓글번호 */
               , RE_REPLY_NO           /* 답글번호 */
               , WRITE_DT              /* 작성일자 */      
               , USER_ID               /* 작성자ID */              
               , RE_REPLY_CNTN         /* 내용 */              
               , DEL_YN                /* 삭제여부 */          
               , REG_USER_ID           /* 등록자 */            
               , REG_DT                /* 등록일자 */          
               )
VALUES ( #{board_div_cd}
       , #{board_no}
       , #{reply_no}
       , #{re_reply_no}
       , DATE_FORMAT(now(), '%Y%m%d')
       , #{user_id}
       , #{re_reply_cntn}
       , 'N'
       , #{user_id}
       , DATE_FORMAT(now(), '%Y%m%d%H%i%s')
       )


  SELECT CASE WHEN USER_ID IS NULL
                   THEN CONCAT('1', LPAD((NVL(MAX(CAST(USER_ID AS UNSIGNED)), 0) + 1), 5,0)) 
              ELSE NVL(MAX(CAST(USER_ID AS UNSIGNED)), 0) + 1
          END AS USER_ID
    FROM USER_INFO  /* 사용자 */

INSERT INTO 
USER_INFO ( USER_ID                        /* 사용자ID */
          , EMAIL_ADDR                     /* 이메일주소 */
          , USER_KOR_NM                    /* 사용자이름 */
          , USER_NIC_NM                    /* 사용자닉네임 */
          , PASSWORD                       /* 사용자비밀번호 */

          , PHONE_NUM                      /* 휴대전화번호 */


          , ROLE_CD                        /* 권한코드 */


          , CHURCH_ROLE_CD                 /* 교회직분코드 */


          , SOCIAL_PROFILE_URL             /* 소셜 프로필 URL */


          , AUTH_YN                        /* 인증여부 */


          , AUTH_KEY                       /* 인증키 */

          , USE_YN                         /* 사용여부 */
          , REG_USER_ID                    /* 등록사용자ID */
          , REG_DT                         /* 등록일자 */
          )
VALUES ( #{user_id}
      , #{email_addr}
      , #{user_nm}
      , #{user_nic_nm}
      , #{password}

      , #{phone_num}


      , #{role_cd}


      , #{church_role_cd}


      , #{social_profile_url}


      , #{auth_yn}  


      , #{auth_key} 

      , 'Y'
      , #{user_id}
      , DATE_FORMAT(now(), '%Y%m%d%H%i%s')
      )

mapper.select:

SELECT USER_ID            /* 사용자ID */
  FROM USER_INFO A
WHERE 1=1
  AND A.EMAIL_ADDR = #{email_addr}
  AND A.PASSWORD   = #{password}
  AND A.AUTH_KEY   = #{auth_key}
  AND A.USE_YN = 'Y'

mapper.update:
UPDATE BOARD
      SET SELECT_CNT = SELECT_CNT + 1
    WHERE 1=1
      AND BOARD_DIV_CD = #{board_div_cd}
      AND BOARD_NO = #{board_no}

mapper.select:

SELECT A.LIKE_EXISTYN
     , B.BOARD_EXISTYN
  FROM (SELECT CASE WHEN COUNT(1) = 0 
                         THEN 'N'
                   ELSE 'Y'
               END AS LIKE_EXISTYN
          FROM BOARD_LIKE
         WHERE 1=1
           AND BOARD_DIV_CD = #{board_div_cd}
           AND CLICK_DIV_CD = #{click_div_cd}
           AND BOARD_NO = #{board_no}
           AND CLICK_USER_ID = #{click_user_id}
           AND (CASE WHEN CHG_DT IS NULL 
                          THEN SUBSTR(REG_DT,1,8)
                     ELSE SUBSTR(CHG_DT,1,8)
                 END) = DATE_FORMAT(now(), '%Y%m%d')
        ) A
 CROSS JOIN (SELECT CASE WHEN COUNT(1) = 0 
                              THEN 'N'
                          ELSE 'Y'
                        END AS BOARD_EXISTYN
               FROM BOARD_LIKE
              WHERE 1=1
                AND BOARD_DIV_CD = #{board_div_cd}
                AND CLICK_DIV_CD = #{click_div_cd}
                AND BOARD_NO = #{board_no}
                AND CLICK_USER_ID = #{click_user_id}
             ) B

mapper.update:
UPDATE BOARD_LIKE
   SET CHG_USER_ID = #{click_user_id}
     , CHG_DT = DATE_FORMAT(now(), '%Y%m%d%H%i%s')
 WHERE 1=1
   AND BOARD_DIV_CD = #{board_div_cd}
   AND CLICK_DIV_CD = #{click_div_cd}
   AND BOARD_NO = #{board_no}
   AND CLICK_USER_ID = #{click_user_id}

mapper.insert:
INSERT INTO 
BOARD_LIKE ( BOARD_DIV_CD                 /* 게시판구분코드 */
           , CLICK_DIV_CD                 /* 조회 좋아요 구분코드*/
           , BOARD_NO                     /* 글번호 */
           , CLICK_USER_ID                /* 선택유저아이디 */
           , DEL_YN                       /* 삭제여부 */
           , REG_USER_ID                  /* 등록자 */
           , REG_DT                       /* 등록일자 */
           )
 VALUES ( #{board_div_cd}
        , #{click_div_cd}
        , #{board_no}
        , #{click_user_id}
        , 'N'
        , #{click_user_id}
        , DATE_FORMAT(now(), '%Y%m%d%H%i%s')
        )
