mapper.insert:

  SELECT NVL(MAX(CAST(FILE_ID AS UNSIGNED)), 0) + 1 
    FROM FILE_UPLOAD_INFO

INSERT INTO 
FILE_UPLOAD_INFO ( FILE_ID            /* 게시판구분코드 */    
                 , FILE_DIV_CD        /* 글번호 */            
                 , ORIGIN_FILE_NM     /* 작성일자 */          
                 , REAL_FILE_NM       /* 작성자아이디 */      
                 , FILE_PATH          /* 제목 */              
                 , FILE_SIZE          /* 파일사이즈 */
                 , USE_YN             /* 사용여부 */
                 , REG_USER_ID        /* 등록자 */            
                 , REG_DT             /* 등록일자 */          
                 )
          VALUES ( #{file_id}
                 , #{file_div_cd}
                 , #{origin_file_nm}
                 , #{real_file_nm}
                 , #{file_path}
                 , #{file_size}
                 , #{use_yn}
                 , #{user_id}
                 , DATE_FORMAT(now(), '%Y%m%d%H%i%s')
                 )

mapper.select:
SELECT FILE_ID
  FROM FILE_UPLOAD_INFO
 WHERE 1=1
   AND ORIGIN_FILE_NM = #{origin_file_nm}
   AND REAL_FILE_NM   = #{real_file_nm}
   AND FILE_DIV_CD    = #{file_div_cd}

SELECT A.BOARD_NO , A.BOARD_DIV_CD , (SELECT CODE_KNM FROM COM_CODE WHERE CD_GRP_ENM = 'BOARD_DIV_CD' AND CODE = A.BOARD_DIV_CD) AS BOARD_DIV_NM , A.USER_ID , (SELECT USER_KOR_NM FROM USER_INFO WHERE USER_ID = A.USER_ID) AS USER_KOR_NM , CASE WHEN A.CHG_DT IS NULL OR A.CHG_DT = '' THEN CONCAT(SUBSTRING(A.WRITE_DT, 3, 2),'-',SUBSTRING(A.WRITE_DT, 5, 2),'-',SUBSTRING(A.WRITE_DT, 7, 2)) ELSE CONCAT(SUBSTRING(A.CHG_DT, 3, 2),'-',SUBSTRING(A.CHG_DT, 5, 2),'-',SUBSTRING(A.CHG_DT, 7, 2)) END WRITE_DT , A.BOARD_TITLE , A.BOARD_CNTN , A.BIBLE_INDEX_CNTN
, A.PHOTO_ID , C.REAL_FILE_NM , CONCAT(C.FILE_PATH,C.REAL_FILE_NM) AS IMG_PATH , A.SELECT_CNT , A.WORSHIP_ORDER_CD , A.VIDEO_DIV_CD , A.YOUTUBE_IFRM_SRC , (SELECT CODE_KNM FROM COM_CODE WHERE CD_GRP_ENM = 'WORSHIP_ORDER_CD' AND CODE = A.WORSHIP_ORDER_CD) AS WORSHIP_ORDER_NM , A.DEL_YN , A.REG_USER_ID , A.REG_DT , (SELECT COUNT(1) FROM BOARD_LIKE IA WHERE 1=1 AND IA.BOARD_DIV_CD = A.BOARD_DIV_CD AND IA.BOARD_NO = A.BOARD_NO AND IA.CLICK_DIV_CD = '02' ) AS FAVORIT_CNT , D.REPLYS_CNT + D.RE_REPLYS_CNT AS REPLYS_CNT FROM BOARD A INNER JOIN USER_INFO B ON A.USER_ID = B.USER_ID AND B.USE_YN = 'Y' LEFT JOIN FILE_UPLOAD_INFO C ON A.PHOTO_ID = C.FILE_ID
INNER JOIN (SELECT IA.BOARD_DIV_CD , IA.BOARD_NO , (SELECT COUNT(1) FROM BOARD_REPLY WHERE 1=1 AND BOARD_DIV_CD = IA.BOARD_DIV_CD AND BOARD_NO = IA.BOARD_NO ) AS REPLYS_CNT , (SELECT COUNT(1) FROM BOARD_RE_REPLY WHERE 1=1 AND BOARD_DIV_CD = IA.BOARD_DIV_CD AND BOARD_NO = IA.BOARD_NO ) AS RE_REPLYS_CNT FROM BOARD IA ) D ON A.BOARD_DIV_CD = D.BOARD_DIV_CD AND A.BOARD_NO = D.BOARD_NO WHERE 1=1 AND A.DEL_YN = 'N' AND (A.BOARD_DIV_CD = #{board_div_cd}
OR A.BOARD_DIV_CD = #{board_div_cd2}
OR A.BOARD_DIV_CD = #{board_div_cd3}
)
AND A.WRITE_DT BETWEEN #{strt_write_dt} AND #{end_write_dt}
AND A.WORSHIP_ORDER_CD = #{worship_order_cd}
AND A.BOARD_TITLE LIKE CONCAT('%', #{board_title}, '%')
AND A.BOARD_CNTN LIKE CONCAT('%', #{board_cntn}, '%')
AND B.USER_KOR_NM LIKE CONCAT('%', #{user_kor_nm}, '%')
ORDER BY CAST(A.BOARD_NO AS UNSIGNED) DESC LIMIT #{start_index}, #{page_size}


SELECT COUNT(1) AS CNT
  FROM BOARD A
 INNER JOIN USER_INFO B 
    ON A.USER_ID = B.USER_ID
   AND B.USE_YN = 'Y'   
 WHERE 1=1
   AND DEL_YN = 'N'
   AND (A.BOARD_DIV_CD = #{board_div_cd}

       OR A.BOARD_DIV_CD = #{board_div_cd2}


       OR A.BOARD_DIV_CD = #{board_div_cd3}

      )

   AND A.WRITE_DT BETWEEN #{strt_write_dt} AND #{end_write_dt}


   AND A.WORSHIP_ORDER_CD = #{worship_order_cd}  


   AND A.BOARD_TITLE LIKE CONCAT('%', #{board_title}, '%')    


   AND A.BOARD_CNTN LIKE CONCAT('%', #{board_cntn}, '%') 


   AND B.USER_KOR_NM LIKE CONCAT('%', #{user_kor_nm}, '%')

mapper.insert:
SELECT NVL(MAX(CAST(board_no AS UNSIGNED)), 0) + 1 FROM BOARD WHERE BOARD_DIV_CD = #{board_div_cd}
SELECT #{board_no}
INSERT INTO BOARD ( BOARD_DIV_CD /* 게시판구분코드 */ , BOARD_NO /* 글번호 */ , WRITE_DT /* 작성일자 */ , USER_ID /* 작성자아이디 */ , BOARD_TITLE /* 제목 */
, BOARD_CNTN /* 내용 */
, BIBLE_INDEX_CNTN /* 성경본문내용 */
, VIDEO_DIV_CD /* 영상구분코드 */
, YOUTUBE_IFRM_SRC /* 유뷰트 IFRAME 소스 */
, WORSHIP_ORDER_CD /* 예배순서코드 */
, VIDEO_ID /* 영상 ID */
, PHOTO_ID /* 이미지 ID */
, SELECT_CNT /* 조회수 */ , DEL_YN /* 삭제여부 */ , CHG_USER_ID /* 수정자 */ , CHG_DT /* 수정일자 */ , REG_USER_ID /* 등록자 */ , REG_DT /* 등록일자 */ ) VALUES ( #{board_div_cd} , #{board_no} , DATE_FORMAT(now(), '%Y%m%d') , #{user_id} , #{board_title}
, #{board_cntn}
, #{bible_index_cntn}
, #{video_div_cd}
, #{youtube_ifrm_src}
, #{worship_order_cd}
, #{video_id}
, #{photo_id}
, '0' , 'N' , '' , '' , #{user_id} , DATE_FORMAT(now(), '%Y%m%d%H%i%s') ) ON DUPLICATE KEY UPDATE BOARD_TITLE = #{board_title} /* 제목 */
, BOARD_CNTN = #{board_cntn} /* 내용 */
, BIBLE_INDEX_CNTN = #{bible_index_cntn} /* 성경본문내용 */
, VIDEO_DIV_CD = #{video_div_cd} /* 영상구분코드 */
, YOUTUBE_IFRM_SRC = #{youtube_ifrm_src} /* 유뷰트 IFRAME 소스 */
, WORSHIP_ORDER_CD = #{worship_order_cd} /* 예배순서코드 */
, VIDEO_ID = #{video_id} /* 영상 ID */
, PHOTO_ID = #{photo_id} /* 이미지 ID */
, CHG_USER_ID = #{user_id} /* 수정자 */ , CHG_DT = DATE_FORMAT(now(), '%Y%m%d%H%i%s') /* 수정일자 */

mapper.update:
UPDATE BOARD
   SET DEL_YN = 'Y'
     , CHG_USER_ID = #{user_id}
     , CHG_DT = DATE_FORMAT(now(), '%Y%m%d%H%i%s')
 WHERE 1=1
   AND BOARD_DIV_CD = #{board_div_cd}
   AND BOARD_NO = #{board_no}

