mapper.insert:

  SELECT CASE WHEN USER_ID IS NULL
                   THEN CONCAT('1', LPAD((NVL(MAX(CAST(USER_ID AS UNSIGNED)), 0) + 1), 5,0)) 
              ELSE NVL(MAX(CAST(USER_ID AS UNSIGNED)), 0) + 1
          END AS USER_ID
    FROM USER_INFO  /* 사용자 */

INSERT INTO 
USER_INFO ( USER_ID                        /* 사용자ID */
          , EMAIL_ADDR                     /* 이메일주소 */
          , USER_KOR_NM                    /* 사용자이름 */
          , USER_NIC_NM                    /* 사용자닉네임 */
          , PASSWORD                       /* 사용자비밀번호 */

          , PHONE_NUM                      /* 휴대전화번호 */


          , ROLE_CD                        /* 권한코드 */


          , CHURCH_ROLE_CD                 /* 교회직분코드 */


          , SOCIAL_PROFILE_URL             /* 소셜 프로필 URL */


          , AUTH_YN                        /* 인증여부 */


          , AUTH_KEY                       /* 인증키 */

          , USE_YN                         /* 사용여부 */
          , REG_USER_ID                    /* 등록사용자ID */
          , REG_DT                         /* 등록일자 */
          )
VALUES ( #{user_id}
      , #{email_addr}
      , #{user_nm}
      , #{user_nic_nm}
      , #{password}

      , #{phone_num}


      , #{role_cd}


      , #{church_role_cd}


      , #{social_profile_url}


      , #{auth_yn}  


      , #{auth_key} 

      , 'Y'
      , #{user_id}
      , DATE_FORMAT(now(), '%Y%m%d%H%i%s')
      )

mapper.select:

SELECT USER_ID            /* 사용자ID */
  FROM USER_INFO A
WHERE 1=1
  AND A.EMAIL_ADDR = #{email_addr}
  AND A.PASSWORD   = #{password}
  AND A.AUTH_KEY   = #{auth_key}
  AND A.USE_YN = 'Y'

mapper.update:
UPDATE USER_INFO A
   SET PASSWORD = #{password}
     , CHG_USER_ID = 'SYSTEM'
     , CHG_DT = DATE_FORMAT(now(), '%Y%m%d%H%i%s')
 WHERE 1=1
   AND A.EMAIL_ADDR = #{email_addr}

UPDATE BOARD
      SET SELECT_CNT = SELECT_CNT + 1
    WHERE 1=1
      AND BOARD_DIV_CD = #{board_div_cd}
      AND BOARD_NO = #{board_no}

mapper.select:

SELECT A.LIKE_EXISTYN       
     , B.BOARD_EXISTYN      
  FROM (SELECT CASE WHEN COUNT(1) = 0 
                         THEN 'N'
                   ELSE 'Y'
               END AS LIKE_EXISTYN
          FROM BOARD_LIKE
         WHERE 1=1
           AND BOARD_DIV_CD = #{board_div_cd}
           AND CLICK_DIV_CD = #{click_div_cd}
           AND BOARD_NO = #{board_no}
           AND CLICK_USER_ID = #{click_user_id}
           AND (CASE WHEN CHG_DT IS NULL 
                          THEN SUBSTR(REG_DT,1,8)
                     ELSE SUBSTR(CHG_DT,1,8)
                 END) = DATE_FORMAT(now(), '%Y%m%d')
        ) A
 CROSS JOIN (SELECT CASE WHEN COUNT(1) = 0 
                              THEN 'N'
                          ELSE 'Y'
                        END AS BOARD_EXISTYN
               FROM BOARD_LIKE
              WHERE 1=1
                AND BOARD_DIV_CD = #{board_div_cd}
                AND CLICK_DIV_CD = #{click_div_cd}
                AND BOARD_NO = #{board_no}
                AND CLICK_USER_ID = #{click_user_id}
             ) B

mapper.update:
UPDATE BOARD_LIKE
   SET CHG_USER_ID = #{click_user_id}
     , CHG_DT = DATE_FORMAT(now(), '%Y%m%d%H%i%s')
 WHERE 1=1
   AND BOARD_DIV_CD = #{board_div_cd}
   AND CLICK_DIV_CD = #{click_div_cd}
   AND BOARD_NO = #{board_no}
   AND CLICK_USER_ID = #{click_user_id}

